import React, { useState } from "react";
import Item from "./item";
import style from'./lista.module.scss';

function Lista(){
    const [tarefas,setTarefas] = useState([{
        tarefa: 'react',
        tempo:'01:10:00'
        },{
         tarefa:'Javascript',
         tempo:'01:30:00'
        },{
         tarefa:'Typescript',
         tempo: '02:20:00'

        }])
    return(
        <aside className={style.listaTarefas}>
            <h2 onClickCapture={()=>{
                setTarefas([...tarefas,{tarefa:'Estudar Estado',tempo:'01:20:00'}])
            }}>Estudos do Dia</h2>
            <ul>
                {tarefas.map((item, index)=>(
                    <Item 
                    key={index}
                       // tarefa={item.tarefa} 
                       // tempo={item.tempo}                  
                       {...item}//puxa todos atributos existente 
                      />
                ))}

            </ul>
        </aside>
    )
}

export default Lista;
