import React from "react";
import style from './botao.module.scss';

interface Props {
    children: string
    type?: 'button' | 'submit' | 'reset' | undefined
    onClick?:() => void;
  }

class Botao extends React.Component<Props> {
    render() {
        const {type = "button", onClik} = this.props;
        return(
            <button onClick={onClik} type={type} className={style.botao}>
                {this.props.children}
            </button>
        )
    }
}
export default Botao;
